// 模板來源 https://leetcode.cn/circle/discuss/mOr1u6/
struct UnionFind {
    vector<int> fa, sz;
    int cc;
    explicit UnionFind(int n) : fa(n), sz(n, 1), cc(n) {
        iota(fa.begin(), fa.end(), 0);
    }
    int find(int x) {
        if (fa[x] != x) fa[x] = find(fa[x]);
        return fa[x];
    }
    bool is_same(int x, int y) {
        return find(x) == find(y);
    }
    bool merge(int x, int y) {
        x = find(x);
        y = find(y);
        if (x == y) return false;
        fa[x] = y;
        sz[y] += sz[x];
        cc -= 1;
        return true;
    }
    int get_size(int x) {
        return sz[find(x)];
    }
};
