// 帶權並查集模板（維護相對距離）
struct WeightedUnionFind {
    vector<int> fa, dis; // dis[x] 表示 x 到代表元的距離
    explicit WeightedUnionFind(int n) : fa(n), dis(n, 0) { iota(fa.begin(), fa.end(), 0); }

    int find(int x) {
        if (fa[x] != x) {
            int root = find(fa[x]);
            dis[x] += dis[fa[x]]; // 遞迴更新 x 到其代表元的距離
            fa[x] = root;
        }
        return fa[x];
    }

    bool same(int x, int y) { return find(x) == find(y); }

    int get_relative_distance(int from, int to) {
        find(from);
        find(to);
        // to-from = (x-from) - (x-to) = dis[from] - dis[to]
        return dis[from] - dis[to];
    }

    // 合併 from 和 to，新增資訊 to - from = value；如果矛盾返回 false
    bool merge(int from, int to, int value) {
        int x = find(from), y = find(to);
        if (x == y) return dis[from] - dis[to] == value;
        // y-x = (to-from) + (y-to) - (x-from)
        dis[x] = value + dis[to] - dis[from]; // 計算 x 到其代表元 y 的距離
        fa[x] = y;
        return true;
    }
};
