// 樹狀陣列模板
struct FenwickTree {
    int n;
    vector<long long> tree; // 使用下標 1 到 n
    explicit FenwickTree(int n) : n(n), tree(n + 1, 0) {}

    // a[i] 增加 val
    void update(int i, long long val) {
        for (; i <= n; i += i & -i) tree[i] += val;
    }

    // 計算字首和 a[1] + ... + a[i]
    long long pre(int i) const {
        long long res = 0;
        for (; i > 0; i &= i - 1) res += tree[i];
        return res;
    }

    // 計算區間和 a[l] + ... + a[r]
    long long query(int l, int r) const {
        if (r < l) return 0;
        return pre(r) - pre(l - 1);
    }
};
